import pdfMake from "pdfmake/build/pdfmake";
import pdfFonts from "pdfmake/build/vfs_fonts";
pdfMake.vfs = pdfFonts.pdfMake.vfs;

const pdf = (machine) => {

  // let itemsTable = {
  //   table: {
  //     body: [
  //       [{text: 'All of the items in this configuration', colSpan: 2}],
  //       []
  //     ]
  //   }
  // }


  let itemsArray = []
  let body = [
    [
      
    ]
  ]

  machine.assemblies.forEach( (assembly) => {
    switch (body[body.length - 1].length) {
      case 0:
        body[body.length - 1].push({text: assembly.name, fontSize: 8, alignment: center});
        break
      case 1:
        body[body.length - 1].push({text: assembly.name, fontSize: 8, alignment: center})
        break
      case 2:
        let newRow = []
        newRow.push({text: assembly.name, fontSize: 8})
        body.push(newRow)

        if (machine.assemblies.indexOf(assembly) === machine.assemblies.length - 1 && body[body.length - 1].length === 1) {
          body[body.length - 1].push(["a placeholder to finish table"])
        }
        break
    }
  })

   
  console.log(body)

  let dd = {
    content: [
      {
        text: 'This paragraph uses header style and extends the alignment property',
        style: 'header',
        alignment: 'center'
      },
      {
        table: {
          widths: [250, 250],
          body: body
        }
      }
      
    ]
  }

  console.log(body)

  pdfMake.createPdf(dd).open();

}

export { pdf }
