import React, { Component } from 'react';
import { connect } from 'react-redux';

class Machine extends Component {

  renderTableRows = () => {
    let itemsArray = []

    const part_types = ["delivery/install", "paper handling", "finishing", "print controller"]
    this.props.machine.assemblies.map( assembly => 
      assembly.items.forEach( item => itemsArray.push(item))
    )

    part_types.forEach( part => {
      return itemsArray.filter( item => item.part_type === part)
    })


