import React from 'react';
import { Link } from "react-router-dom";

function Navbar() {

  const isActive = (path) => {
    if (path === this.props.location.pathname) {
      return "bg-blue-500 px-3"
    } else {
      return "bg-blue-200 px-3"
    }
  }

  return (
    <div className="flex space-x-10 justify-center">
      <button className={isActive('/')}><Link to="/" >New Proposal</Link></button>
      <button className={isActive('/about')}><Link to="/about">All Proposals</Link></button>
      <button className={isActive('/edit')}><Link to="/edit">Edit Proposal</Link></button>
    </div>
  );
}

export default Navbar;
