// import { combineReducers } from 'redux';

function newMachineReducer(state = {
  machine: {
    model: '',
    items: []
  }
}, action) {
  switch (action.type) {
    case 'ADD_MODEL':
      return Object.assign({}, {...state}, {modelId: action.modelId})
    case 'ADD_ASSEMBLY':
      debugger;
      return {
        ...state,
          machine: {
            ...state.machine,
              items: [...state.machine.items, action.assembly]
          }
      }
    default:
      return {...state}
  }
}

export default newMachineReducer;
