import pdfMake from "pdfmake/build/pdfmake";
import pdfFonts from "pdfmake/build/vfs_fonts";
pdfMake.vfs = pdfFonts.pdfMake.vfs;

const pdf = (machine) => {

  let itemsTable = {
    table: {
      body: [
        [{text: 'All of the items in this configuration', colSpan: 2}],
        []
      ]
    }
  }

  const addMachineItems = (itemsTable, machineAssemblies) => {

    let newItemsTable = itemsTable
    // This needs to be simpler. Items array should be defined in state because it is used in multiple other places.
    let itemsArray = []

    machineAssemblies.forEach( (mi) => {
      mi.items.forEach( item => {
        itemsArray.push(item)
      })
      
    })

    itemsArray.forEach( item => {
      switch (newItemsTable.table.body[newItemsTable.table.body.length - 1].length) {
        case 0:
            newItemsTable = {
          ...newItemsTable,
          table: {
            ...newItemsTable.table,
            body: [
              ...newItemsTable.table.body,
              [item.description]     
            ]
          }
        }
      }
    })

    return addItemsToDd(dd, newItemsTable)
  }


  const addItemsToDd = (documentData, machineItems) => {
   const newDd = {
     ...documentData,
     content: [
       ...documentData.content,
       machineItems
     ]
   }

    return newDd
  }

  let dd = {
    content: [
      {
        text: 'This paragraph uses header style and extends the alignment property',
        style: 'header',
        alignment: 'center'
      }
      
    ]
  }

  console.log("addMachineItems", addMachineItems(itemsTable, machine.assemblies))
  
  pdfMake.createPdf(addMachineItems(itemsTable, machine.assemblies)).open();

}

export { pdf }
