import pdfMake from "pdfmake/build/pdfmake";
import pdfFonts from "pdfmake/build/vfs_fonts";
pdfMake.vfs = pdfFonts.pdfMake.vfs;

const pdf = (machine) => {

  let itemsTable = {
    table: {
      body: [
        ['All of the items in this configuration']
      ]
    }
  }

  const addMachineItems = (table, machineItems) => {

    debugger;
    machineItems.forEach( (mi) => {
      itemsTable = {
        ...table,
          table: {
            body: [
              ...table.body,
              
            ]
          
        }
      }
    })
  }
  const addItemsToDd = (documentData, machineItems) => {
   const newDd = {
     ...documentData,
     content: [
       ...documentData.content,
       machineItems
     ]
   }

    return newDd
  }

  let dd = {
    content: [
      {
        text: 'This paragraph uses header style and extends the alignment property',
        style: 'header',
        alignment: 'center'
      }
      
    ]
  }
  
  debugger;

  
  pdfMake.createPdf(() => addItemsToDd).open();

}

export { pdf }
