// import { combineReducers } from 'redux';

function newMachineReducer(state = {
  machine: {
    assemblies: []
  },
  model: {
    allAssemblies: [],
    remainingAssemblies: []
  }
}, action) {
  switch (action.type) {
    case 'ADD_MODEL':
      return Object.assign({}, {...state}, {modelId: action.modelId})
    case 'ADD_ALL_ASSEMBLIES':
      return {
        ...state,
          model: {
            ...state.model,
              allAssemblies: action.assemblies
          }
      }
    case 'ADD_ASSEMBLY':
      return {
        ...state,
          machine: {
            ...state.machine,
              items: [...state.machine.items, action.assembly]
          },
          model: {
            ...state.model,
              remainingAssemblies: []
          }
      }
    default:
      return {...state}
  }
}

export default newMachineReducer;
