import React, { Component } from 'react';
import numeral from 'numeral';

class MachineOverview extends Component {
  render() {

    let priceArray = [];

    return this.props.machineAssemblies.length > 0 ? this.props.machineAssemblies.forEach(assembly => assembly.items.forEach( item => priceArray.push(item.branchFloorPrice))) : 0

    const totalPrice = priceArray.reduce((a, b) => a + b, 0)
    // const SellingTotal 
    return (
      <>
        <h1 className="text-center">Machine Overview</h1>
        <h1 className="text-center">{numeral(totalPrice).format('$0,0.00')}</h1>
      </>
    );
  }
}

export default MachineOverview;
