  return (
    <div className="flex space-x-10 justify-center">
      <button className={isActive('/', this.props.location.pathname)}><Link to="/" >New Proposal</Link></button>
      <button className={isActive('/about', this.props.location.pathname)}><Link to="/about">All Proposals</Link></button>
      <button className={isActive('/edit', this.props.location.pathname)}><Link to="/edit">Edit Proposal</Link></button>
    </div>
  );
}

export default Navbar;
