import pdfMake from "pdfmake/build/pdfmake";
import pdfFonts from "pdfmake/build/vfs_fonts";
pdfMake.vfs = pdfFonts.pdfMake.vfs;

const pdf = (machine) => {

  // let itemsTable = {
  //   table: {
  //     body: [
  //       [{text: 'All of the items in this configuration', colSpan: 2}],
  //       []
  //     ]
  //   }
  // }

  const addMachineItems = (itemsTable) => {

    let itemsArray = [[]]

    machine.assemblies.forEach( (mi) => {
      mi.items.forEach( item => {
        itemsArray.push(item)
      })
    })

    itemsArray.forEach( item => {
      switch (newTable.table.body[newTable.table.body.length - 1].length) {
        case 0:
          let newElement = []
          newElement.push(item.description)
          newTable.table.body[newTable.table.body.length - 1].push(newElement);
          break
        case 1:
          let newArrElement = []
          newArrElement.push(item.description)
          newTable.table.body[newTable.table.body.length - 1].push(newArrElement);
          break
        case 2:
          let newRow = [[]]
          newRow[0].push(item.description)
          newTable.table.body.push(newRow)
          break
      }
    })

    return addItemsToDd(dd, newTable)
  }

  let dd = {
    content: [
      {
        text: 'This paragraph uses header style and extends the alignment property',
        style: 'header',
        alignment: 'center'
      },
      {
        table: {
          body: itemsArray
        }
      }
      
    ]
  }
  
  pdfMake.createPdf(dd).open();

}

export { pdf }
