import React, { Component } from 'react';
import { Link } from "react-router-dom";

class Navbar extends Component {

  isActive = (path) => {
    if (path === this.props.location) {
      return "bg-blue-500 px-3"
    } else {
      return "bg-blue-200 px-3"
    }
  }

  pathname = this.props.location

  render() {
    return (
      <div className="flex space-x-10 justify-center">
        <button className={this.isActive('/')}><Link to="/" >New Proposal</Link></button>
        <button className={this.isActive('/about')}><Link to="/about">All Proposals</Link></button>
        <button className={this.isActive('/about')}><Link to="/edit">Edit Proposal</Link></button>
      </div>
    );
  }
  }

export default Navbar;
