import React from 'react';
import { connect } from 'react-redux';

function machineAssembly(props) {
  return (
    <div className="w-full h-1/5">
      <span className="pl-4 underline">{this.props.assemblyName}</span>
      {renderMachineAssemblies()}
    </div>
  );
}

function renderMachineAssemblies() {
  const assemblies = this.props.assemblies.filter( assembly => assembly.assembly_type == this.props.assemblyName)

  assemblies.map( assembly => {
    <div>
      <h1>{assembly.name}</h1>
      <ul>
        {assembly.items.map( item => {
          <li>{item.description}</li>
        })}
      </ul>
    </div>
  })
}

const mapStateToProps = (state) => (
  {assemblies: state.machine.items}
)

const mapDispatchToProps = (dispatch) => {
  
}

export default connect(mapDispatchToProps, mapStateToProps)(machineAssembly);
