import React, { Component } from 'react';
import { connect } from 'react-redux';

class EnginePick extends Component {

  state = {
    selectAssemblies: [],
    selectedParts: []
  }

  componentDidMount() {
    console.log("mounted")
    fetch(`http://localhost:3000/models/${this.props.modelId}/assemblies`)
    .then(resp => resp.json())
    .then(json => {
      const mainUnitAssemblies = json.filter( assembly => assembly.assembly_type === "main unit")

      this.setState({
        selectAssemblies: mainUnitAssemblies
      })
    })
  
  }

  renderAssemblies = () => {
    return this.state.selectAssemblies.map( assembly => {
      return(
        <div className="assembly bg-blue-200 w-1/4 h-32 flex align-items-center">
          <h1 className="text-center">{assembly.name}</h1>
        </div>
      )
    })
  }

  render() {
    return (
      <div className="w-11/12 mx-auto h-3/4 flex">
        <div className="w-3/4 flex flex-wrap space-x-4 place-self-center" id="select-assemblies">
          {this.renderAssemblies()}
        </div>
        <div className="w-1/6 h-full" id="selected-items">

        </div>

      </div>
    );
  }
}

const mapStateToProps = state => (
  {modelId: state.modelId}
)

const mapDispatchToProps = dispatch => (
  {}
)



export default connect(mapStateToProps, mapDispatchToProps)(EnginePick)
