import React, { Component } from 'react';
import { connect } from 'react-redux';

class EnginePick extends Component {

  state = {
    mainUnitAssemblies: []
  }

  componentDidMount() {
    console.log("mounted")
    fetch(`http://localhost:3000/models/${this.props.modelId}/assemblies`)
    .then(resp => resp.json())
    .then(json => {
      const mainUnitAssemblies = json.filter( assembly => assembly.assembly_type === "main_unit")

      this.setState({
        mainUnitAssemblies: mainUnitAssemblies
      })
    })
  
  }

  renderAssemblies = () => {
    return this.state.mainUnitAssemblies.map( assembly => {
      return(
        <div class="assembly bg-blue-200 w-36 h-36">
          <h1></h1>
        </div>
      )
    })
  }

  render() {
    return (
      <div className="w-5/6 mx-auto h-3/4 flex justify-around">
        <div className="w-3/5 h-full" id="select-assemblies">

        </div>
        <div className="w-3/5 h-full" id="selected-items">

        </div>
        Pick Engine
      </div>
    );
  }
}

const mapStateToProps = state => (
  {modelId: state.modelId}
)

const mapDispatchToProps = dispatch => (
  {}
)



export default connect(mapStateToProps, mapDispatchToProps)(EnginePick)
