import React, { Component } from 'react';

class Proposals extends Component {

  state = { proposals: []}

  componentDidMount() {
    fetch("http://localhost:3000/proposals")
    .then( resp => resp.json())
    .then( json => {
      this.state = { proposals: json }
    })
  }

  renderProposalCards = () => {
    if (this.state.proposals.length > 0) {
      this.state.proposals.forEach( proposal => {
        return (
          <div className="w-1/6 h-12 border-2 border-black rounded-md flex flex-col place-items-center">
            <h1>{proposal.customer_name}</h1>
          </div>
        )
      })
    }
  }

  render() {
    return (
      <div>
        {this.renderProposalCards()}
      </div>
    );
  }
}

export default Proposals;
