import { combineReducers } from 'redux';

function newMachineReducer(state = {
  model: ''
}, action) {
  debugger;
  switch (action.type) {
    case 'ADD_MODEL':
      return Object.assign({}, ...state, {action})
    default:
      return {...state}
  }
}

export default newMachineReducer;
