import pdfMake from "pdfmake/build/pdfmake";
import pdfFonts from "pdfmake/build/vfs_fonts";
pdfMake.vfs = pdfFonts.pdfMake.vfs;

const pdf = (machine) => {

  // let itemsTable = {
  //   table: {
  //     body: [
  //       [{text: 'All of the items in this configuration', colSpan: 2}],
  //       []
  //     ]
  //   }
  // }


  let itemsArray = []
  let body = [
    [
      
    ]
  ]

  machine.assemblies.forEach( (mi) => {
    mi.items.forEach( item => {
      itemsArray.push(item)
    })
  })

  itemsArray.forEach( item => {
    switch (body[body.length - 1].length) {
      case 0:

        body[body.length - 1].push({text: item.description, fontSize: 8});
        break
      case 1:
        body[body.length - 1].push({text: item.description, fontSize: 8})
        break
      case 2:
        let newRow = []
        newRow.push({text: item.description, fontSize: 8})
        body.push(newRow)

        if (itemsArray.indexOf(item) === itemsArray.length - 1 && body[body.length - 1].length === 1) {
          body[body.length - 1].push(["a placeholder to finish table"])
        }
        break
    }
  })
   
  console.log(body)

  let dd = {
    content: [
      {
        text: 'This paragraph uses header style and extends the alignment property',
        style: 'header',
        alignment: 'center'
      },
      {
        table: {
          body: body
        }
      }
      
    ]
  }

  console.log(body)

  pdfMake.createPdf(dd).open();

}

export { pdf }
