// import { combineReducers } from 'redux';

function newMachineReducer(state = {
  machine: {
    model: '',
    items: []
  }
}, action) {
  switch (action.type) {
    case 'ADD_MODEL':
      return Object.assign({}, {...state}, {modelId: action.modelId})
    case 'ADD_ASSEMBLY':
      return Object.assign({}, {...state}, {
        machine: {
          items: []
        }
      })
    default:
      return {...state}
  }
}

export default newMachineReducer;
